graphql.query:
query {
  vessels(
      shipType: [GENERAL_CARGO,CONTAINER]
        first: 10  
    ) {
    pageInfo {
      hasNextPage
      correlationId
      endCursor
    }
    nodes {
      id
      updateTimestamp
      staticData {
        aisClass
        flag
        name
        callsign
        timestamp
        updateTimestamp
        shipType
        shipSubType
        mmsi
        callsign
        dimensions {
          a
          b
          c
          d
          width
          length
        }
      }
      lastPositionUpdate {
        accuracy
        collectionType
        course
        heading
        latitude
        longitude
        maneuver
        navigationalStatus
        rot
        speed
        timestamp
        updateTimestamp
      }
      currentVoyage {
        destination
        draught
        eta
        matchedPort {
          matchScore
          port {
            name
            unlocode
            centerPoint {
              latitude
              longitude
            }
          }
        }
        timestamp
        updateTimestamp
      }
    }
  }
}

